const panesDefaults: PanesDefaults = { resizable: false };

// Configuring global defaults for all panes in the app.
@NgModule({
  //...
  providers: [
    // ...
    { provide: PANES_DEFAULTS, useValue: panesDefaults }
  ]
})
export class AppModule {}

// Configuring defaults for all child panes of some component.
@Component({
  // ...
  providers: [{ provide: PANES_DEFAULTS, useValue: panesDefaults }]
})
class SomeComponent {}
